const t=document.getElementById("mainPage"),n=document.getElementById("fontPage"),s=document.getElementById("fontSettingsBtn"),c=document.getElementById("backBtn");s.addEventListener("click",()=>{t.classList.add("hidden"),n.classList.remove("hidden")});c.addEventListener("click",()=>{n.classList.add("hidden"),t.classList.remove("hidden")});const e=document.getElementById("toggleRounderCards");chrome.storage.sync.get(["roundedCards"],d=>{e.checked=!!d.roundedCards});e.addEventListener("change",()=>{chrome.storage.sync.set({roundedCards:e.checked})});
