const s=document.getElementById("mainPage"),r=document.getElementById("fontPage"),g=document.getElementById("fontSettingsBtn"),m=document.getElementById("backBtn");g.addEventListener("click",()=>{s.classList.add("hidden"),r.classList.remove("hidden")});m.addEventListener("click",()=>{r.classList.add("hidden"),s.classList.remove("hidden")});const a=document.querySelectorAll('input[name="font"]');chrome.storage.sync.get(["selectedFont"],e=>{e.selectedFont&&a.forEach(c=>{c.dataset.font===e.selectedFont&&(c.checked=!0)})});a.forEach(e=>{e.addEventListener("change",()=>{e.checked&&chrome.storage.sync.set({selectedFont:e.dataset.font})})});const t=document.getElementById("toggleRounderCards");chrome.storage.sync.get(["roundedCards"],e=>{t.checked=!!e.roundedCards});t.addEventListener("change",()=>{chrome.storage.sync.set({roundedCards:t.checked})});const n=document.getElementById("toggleGradientCards");chrome.storage.sync.get(["gradientCards"],e=>{n.checked=!!e.gradientCards});n.addEventListener("change",()=>{chrome.storage.sync.set({gradientCards:n.checked})});const d=document.getElementById("toggleHideAnnouncements");chrome.storage.sync.get(["hideAnnouncements"],e=>{d.checked=!!e.hideAnnouncements});d.addEventListener("change",()=>{chrome.storage.sync.set({hideAnnouncements:d.checked})});const o=document.getElementById("toggleRemoveSidebarLogo");chrome.storage.sync.get(["removeSidebarLogo"],e=>{o.checked=!!e.removeSidebarLogo});o.addEventListener("change",()=>{chrome.storage.sync.set({removeSidebarLogo:o.checked})});
