const c=document.getElementById("mainPage"),s=document.getElementById("fontPage"),o=document.getElementById("fontSettingsBtn"),a=document.getElementById("backBtn");o.addEventListener("click",()=>{c.classList.add("hidden"),s.classList.remove("hidden")});a.addEventListener("click",()=>{s.classList.add("hidden"),c.classList.remove("hidden")});const n=document.getElementById("toggleRounderCards");chrome.storage.sync.get(["roundedCards"],e=>{n.checked=!!e.roundedCards});n.addEventListener("change",()=>{chrome.storage.sync.set({roundedCards:n.checked})});const t=document.getElementById("toggleGradientCards");chrome.storage.sync.get(["gradientCards"],e=>{t.checked=!!e.gradientCards});t.addEventListener("change",()=>{chrome.storage.sync.set({gradientCards:t.checked})});const d=document.getElementById("toggleHideAnnouncements");chrome.storage.sync.get(["hideAnnouncements"],e=>{d.checked=!!e.hideAnnouncements});d.addEventListener("change",()=>{chrome.storage.sync.set({hideAnnouncements:d.checked})});
