const n=document.getElementById("mainPage"),o=document.getElementById("fontPage"),c=document.getElementById("fontSettingsBtn"),d=document.getElementById("backBtn"),t=document.getElementById("logomarkToggle");chrome.storage.sync.get(["hideLogomark","fontChoice"],e=>{t.checked=e.hideLogomark??!1,e.fontChoice&&document.querySelector(`input[name="font"][value="${e.fontChoice}"]`)?.setAttribute("checked",!0)});t.addEventListener("change",()=>{chrome.storage.sync.set({hideLogomark:t.checked})});c.addEventListener("click",()=>{n.classList.add("hidden"),o.classList.remove("hidden")});d.addEventListener("click",()=>{o.classList.add("hidden"),n.classList.remove("hidden")});document.querySelectorAll('input[name="font"]').forEach(e=>{e.addEventListener("change",()=>{chrome.storage.sync.set({fontChoice:e.value})})});
